export default function App() {
  const [number, setNumber] = useState(0)

  const add = () => {
    setNumber(
      number = number +1
    )
  }
  return (
    <div>
      <h1>{number}</h1>
      <button onClick={ add }>add</button>
      <button onClick={ add }>substract</button>
      <button onClick={ add }>reset</button>
    </div>
  )
}
