import React, { useState } from 'react'
import './App.css';

export default function App() {
  let [counter, setCounter] = useState(0)

  const add = () => {
    setCounter( ++counter )
  }
  const sub = () => {
    setCounter( --counter )
  }
  const res = () => {
    setCounter( 0 )
  }

  return (
    <>

    <div>
      <h1>{counter}</h1>
      <button onClick={ add }>add</button>
      <button onClick={ sub }>substract</button>
      <button onClick={ res }>reset</button>
    </div>
    <br/>
    <div>
      <input/> +
      <input /> = 
      <input />
      <button>Sumar</button>
    </div>
    </>
  )
}
